INSERT INTO plants (id, generic_name, specific_name, description, thumbnail_photo_url, large_plant_card_photo_url, when_to_plant, water_needs, sunlight_needs, lowest_temp_tolerance, highest_temp_tolerance, how_far_apart_to_plant, how_deep_to_plant, how_long_until_mature) VALUES (1, 'Cucumber', 'Cucumis sativus', 'An easy-care vegetable that loves sun and water, cucumbers grow quickly as long as they receive consistent watering and warmth. Don’t let cucumbers get too large before you pick or they will taste bitter!', 'https://images.pexels.com/photos/128420/pexels-photo-128420.jpeg?auto=compress&cs=tinysrgb&w=1600', 'https://images.pexels.com/photos/128420/pexels-photo-128420.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1', 'April to June', 3, 'Daily 6-8 hours', 1, 40, '6 inches apart', 'One inch deep', '50 to 70 days');
INSERT INTO plants (id, generic_name, specific_name, description, thumbnail_photo_url, large_plant_card_photo_url, when_to_plant, water_needs, sunlight_needs, lowest_temp_tolerance, highest_temp_tolerance, how_far_apart_to_plant, how_deep_to_plant, how_long_until_mature) VALUES (2, 'Cherry Tomato', 'Solanum lycopersicum', 'These succulent, bite-sized jewels concentrate all the flavor of a full sized tomato into a package that is less than 2" in diameter. They are sweeter, perfectly shaped, and have thinner skin than regular tomatoes.', 'https://images.pexels.com/photos/8857067/pexels-photo-8857067.jpeg?auto=compress&cs=tinysrgb&w=1600', 'https://images.pexels.com/photos/8857067/pexels-photo-8857067.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1', 'May to August', 3, 'Daily 6-8 hours', 0, 35, '24 inches apart', 'At least 1/2 inch deep', '55 to 65 days');
INSERT INTO plants (id, generic_name, specific_name, description, thumbnail_photo_url, large_plant_card_photo_url, when_to_plant, water_needs, sunlight_needs, lowest_temp_tolerance, highest_temp_tolerance, how_far_apart_to_plant, how_deep_to_plant, how_long_until_mature) VALUES (3, 'Kale', 'Brassica oleracea', 'Ever been told to “eat your greens?” Kale is one of the most nutritious vegetables you can grow. It’s crammed with vitamins and powerful antioxidants, and it tastes de-licious.', 'https://images.pexels.com/photos/134877/pexels-photo-134877.jpeg?auto=compress&cs=tinysrgb&w=1600', 'https://images.pexels.com/photos/134877/pexels-photo-134877.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1', 'May to early July', 1, 'Daily at least 6 hours', 10, 25, '1 to 2 feet apart', 'At least 1/2 inch deep', '3 months');